import React, { useState, useEffect } from 'react'
import axios from 'axios'

// Comps
import CompaniItem from './CompaniItem'

const CompaniesList = () => {
  const [companies, setCompanies] = useState(null)

  useEffect(() => { callAllCompanies() }, [])


  const callAllCompanies = async () => {
    let token = localStorage.getItem('token')
    let config = {
      headers: {
        'auth-token': token
      }
    }
    const res = await axios.get('http://localhost:5000/api/company', config)
    const data = await res.data

    console.log(data)
    setCompanies(data)
  }

  return (
    <div className=''>
      <h1>CompaniesList</h1>
      {!companies ? <h3>List empty</h3> :
        companies.map((company, index) => <CompaniItem key={index} company={company} />)}
    </div>
  )
}

export default CompaniesList
